-- ----------------------------
-- Triggers structure for table ez_mitra_deposit_transaction
-- ----------------------------
DROP TRIGGER IF EXISTS `update_saldo`;
delimiter ;;
CREATE TRIGGER `update_saldo` AFTER INSERT ON `ez_mitra_deposit_transaction` FOR EACH ROW BEGIN
	if(new.type = 'IN') THEN
		UPDATE ez_mitra_saldo SET saldo= saldo+new.amount WHERE mitra_id= new.mitra_id;
	END IF;
	if(new.type = 'OUT') THEN
		UPDATE ez_mitra_saldo SET saldo= saldo-new.amount WHERE mitra_id= new.mitra_id;
	END IF;
END
;;
delimiter ;

